import React from "react";

export default function Toggle() {
  function switchMode(event) {
      document.body.classList.add("lightMode");
  }

  return (
    <div className="toggle">
      <div className="form-check form-switch">
      <input className="form-check-input" type="checkbox" id="flexSwitchCheckChecked" onClick={switchMode}></input>
        <label className="form-check-label" for="flexSwitchCheckChecked">Dark/Light Mode</label>
      </div>
    </div>
  );
}
